import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})

@Injectable({
  providedIn: 'root'
})

export class SignupComponent {

  constructor(private http: HttpClient) { }

   fazerSolicitacaoPost() {
    const url = 'http://localhost:8080/user'; // Substitua pela URL do seu servidor Node.js
    const dados = { chave: 'valor' }; // Substitua pelos dados que deseja enviar no POST

    const headers = new HttpHeaders({
      'Content-Type': 'application/json'
    });

    this.http.post(url, dados, { headers }).subscribe(
      (response) => {
        console.log('Resposta do servidor:', response);
        // Faça algo com a resposta do servidor Node.js
      },
      (error) => {
        console.error('Erro na solicitação:', error);
        // Trate o erro, se necessário
      }
    );
  }

}
