import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {
  nome: string = '';
  email: string = '';
  password: string = '';
  

  constructor(private http: HttpClient) { }

  enviarRegistro() {
    const url = 'http://localhost:8080/user'; // Substitua pela URL do seu servidor Node.js

    const dados = {
      nome: this.nome,
      email: this.email,
      password: this.password,
      preferences: 0,
      ranking: 0,
      points:0,
      
    };

    this.http.post(url, dados).subscribe(
      (response) => {
        console.log('Resposta do servidor:', response);
        // Faça algo com a resposta do servidor Node.js
      console.log('Dados a serem enviados:', dados);
      },
      (error) => {
        console.error('Erro na solicitação:', error);
        // Trate o erro, se necessário
      }
    );
  }
}
<div class="signup-container">
  <div class="signup-box">
    <h1>Criar Conta</h1>
    <form (submit)="enviarRegistro()">
      >
      <div class="input-group">
        <label for="nome">Nome Completo:</label>
        <input
          name="name"
          type="text"
          id="nome"
          placeholder="Digite seu nome completo"
        />
      </div>
      <div class="input-group">
        <label for="email">E-mail:</label>
        <input
          name="email"
          type="email"
          id="email"
          placeholder="Digite seu e-mail"
        />
      </div>
      <div class="input-group">
        <label for="password">Senha:</label>
        <input
          name="password"
          type="password"
          id="password"
          placeholder="Digite sua senha"
        />
      </div>
      <div class="input-group">
        <label for="confirmaSenha">Confirmação de Senha:</label>
        <input
          type="password"
          id="confirmaSenha"
          placeholder="Confirme sua senha"
        />
      </div>
      <div class="input-group">
        <label for="dataNascimento">Data de Nascimento:</label>
        <input type="date" id="dataNascimento" />
      </div>
      <input type="submit" class="signup-button" />Criar Conta />
      <button class="back-button" routerLink="/">Voltar</button>
    </form>
  </div>
</div>
