import { Component } from '@angular/core';

@Component({
  selector: 'app-initialactions',
  templateUrl: './initialactions.component.html',
  styleUrls: ['./initialactions.component.css']
})
export class InitialactionsComponent {

}
<div class="signup-container">
  <div class="black-box">
    <h1>Inscreva-se Já</h1>
    <p>Preencha o formulário abaixo para criar uma conta:</p>
    <div class="input-group"></div>
    <div class="input-group"></div>
    <button class="login-button">Login</button>
    <p>ou</p>
    <button class="signup-button">Sign Up</button>
  </div>
</div>
