import { Component } from '@angular/core';

@Component({
  selector: 'app-initialactions',
  templateUrl: './initialactions.component.html',
  styleUrls: ['./initialactions.component.css']
})
export class InitialactionsComponent {

}
<div class="signup-container">
  <div class="black-box">
    <h1>Inscreva-se Já</h1>
    <p>
      Crie sua conta para fazer parte da comunidade fatecana e ter acesso aos
      cursos gratuitos!
    </p>

    <button routerLink="/signup" class="login-button">Sign Up</button>
    <p>Já possui conta cadastrada? Acesse aqui</p>
    <button routerLink="/login" class="signup-button">Login</button>
  </div>
</div>
